import React, { useEffect, useState, useContext } from 'react';
import { useTodo } from '../context/todoContext';

const Todo = () => {
  const [text, setText] = useState('');
  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState('');

  const { todos, dispatch } = useTodo();

  const onSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:4000/api/todos', {
      method: 'POST',
      body: JSON.stringify({ todo: text }),
      headers: { 'Content-Type': 'application/json' },
    });
    const data = await response.json();
    dispatch({ type: 'CREATE_TODO', payload: data });
    setText('');
  };

  const handleEdit = async (e) => {
    console.log({ editId });
    e.preventDefault();
    // const response = await fetch('http://localhost:4000/api/todos/' + editId, {
    //   method: 'PATCH',
    // });
    // const data = await response.json();
    // if (response.ok) {
    //   dispatch({ type: 'UPDATE_TODO', payload: data });
    //   setEditMode(false);
    //   setText('');
    // }
  };

  const handleDelete = async (id) => {
    const response = await fetch('http://localhost:4000/api/todos/' + id, {
      method: 'DELETE',
    });
    const data = await response.json();
    if (response.ok) {
      dispatch({ type: 'DELETE_TODO', payload: data });
    }
  };

  useEffect(() => {
    const getTodos = async () => {
      const response = await fetch('http://localhost:4000/api/todos');
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: 'SET_TODOS', payload: data });
      }
    };
    getTodos();
  }, []);

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }}
    >
      <form onSubmit={() => (editMode ? handleEdit : onSubmit)}>
        <input
          type='text'
          placeholder='Enter a todo...'
          required
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button type='submit'>{editMode ? 'Edit' : 'Add'}</button>
      </form>
      <div>
        {todos?.length > 0 &&
          todos.map((todo) => {
            return (
              <div key={todo._id}>
                <div>{todo.todo}</div>
                <button
                  onClick={() => {
                    setEditMode(true);
                    setEditId(todo._id);
                    setText(todo.todo);
                  }}
                >
                  Edit
                </button>
                <button onClick={() => handleDelete(todo._id)}>Delete</button>
              </div>
            );
          })}
      </div>
    </div>
  );
};

export default Todo;
