import React, { useEffect, useState } from 'react';
import { useTodo } from '../context/todoContext';
import { useAuthContext } from '../context/authContext';
import { Link } from 'react-router-dom';

const Todo = () => {
  const [text, setText] = useState('');
  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState('');
  const [loading, setLoading] = useState(true);

  const { todos, dispatch } = useTodo();
  const { user, dispatch: dispatchUser } = useAuthContext();
  const onSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:4000/api/todos', {
      method: 'POST',
      body: JSON.stringify({ todo: text }),
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${user.token}`,
      },
    });

    const data = await response.json();
    console.log({ response, data });
    dispatch({ type: 'CREATE_TODO', payload: data });
    setText('');
  };

  const handleEdit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:4000/api/todos/' + editId, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${user.token}`,
      },
      body: JSON.stringify({ todo: text }),
    });
    const data = await response.json();

    if (response.ok) {
      dispatch({ type: 'UPDATE_TODO', payload: { ...data, todo: text } });
      setEditMode(false);
      setText('');
    }
  };

  const handleDelete = async (id) => {
    const response = await fetch('http://localhost:4000/api/todos/' + id, {
      method: 'DELETE',
      headers: { 'Authorization': `Bearer ${user.token}` },
    });
    const data = await response.json();
    if (response.ok) {
      dispatch({ type: 'DELETE_TODO', payload: data });
    }
  };

  useEffect(() => {
    const getTodos = async () => {
      const response = await fetch('http://localhost:4000/api/todos', {
        headers: { 'Authorization': `Bearer ${user.token}` },
      });
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: 'SET_TODOS', payload: data });
        setLoading(false);
      }
    };
    if (user) {
      getTodos();
    }
  }, [dispatch, user]);

  if (loading) {
    return <p>Loading...</p>;
  }
  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }}
    >
      {user && <div>{user.email}</div>}
      {!user && <Link to='/login'>Login</Link>}
      {user && (
        <button
          onClick={() => {
            localStorage.removeItem('user');
            dispatchUser({ type: 'LOGOUT' });
            dispatch({ type: 'SET_TODOS', payload: null });
          }}
        >
          Logout
        </button>
      )}
      <form onSubmit={editMode ? handleEdit : onSubmit}>
        <input
          type='text'
          placeholder='Enter a todo...'
          required
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button type='submit'>{editMode ? 'Edit' : 'Add'}</button>
      </form>
      <div>
        {todos?.length > 0 &&
          todos.map((todo) => {
            return (
              <div key={todo._id}>
                <div>{todo.todo}</div>
                <button
                  onClick={() => {
                    setEditMode(true);
                    setEditId(todo._id);
                    setText(todo.todo);
                  }}
                >
                  Edit
                </button>
                <button onClick={() => handleDelete(todo._id)}>Delete</button>
              </div>
            );
          })}
      </div>
    </div>
  );
};

export default Todo;
