import React, { useEffect, useState } from 'react';
import { useTodo } from '../context/todoContext';
import { useAuthContext } from '../context/authContext';
import { Link } from 'react-router-dom';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import TextField from '@mui/material/TextField';
import { Box } from '@mui/material';
import Button from '@mui/material/Button';

const Todo = () => {
  const [text, setText] = useState('');
  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState('');
  const [loading, setLoading] = useState(true);

  const { todos, dispatch } = useTodo();
  const { user, dispatch: dispatchUser } = useAuthContext();
  const onSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:4000/api/todos', {
      method: 'POST',
      body: JSON.stringify({ todo: text }),
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${user.token}`,
      },
    });

    const data = await response.json();
    dispatch({ type: 'CREATE_TODO', payload: data });
    setText('');
  };

  const handleEdit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:4000/api/todos/' + editId, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${user.token}`,
      },
      body: JSON.stringify({ todo: text }),
    });
    const data = await response.json();

    if (response.ok) {
      dispatch({ type: 'UPDATE_TODO', payload: { ...data, todo: text } });
      setEditMode(false);
      setText('');
    }
  };

  const handleDelete = async (id) => {
    const response = await fetch('http://localhost:4000/api/todos/' + id, {
      method: 'DELETE',
      headers: { 'Authorization': `Bearer ${user.token}` },
    });
    const data = await response.json();
    if (response.ok) {
      dispatch({ type: 'DELETE_TODO', payload: data });
    }
  };

  useEffect(() => {
    const getTodos = async () => {
      const response = await fetch('http://localhost:4000/api/todos', {
        headers: { 'Authorization': `Bearer ${user.token}` },
      });
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: 'SET_TODOS', payload: data });
        setLoading(false);
      }
    };
    if (user) {
      getTodos();
    }
  }, [dispatch, user]);

  return (
    <div
      style={{
        width: '100vw',
        height: '100vh',
        backgroundColor: '#0077b6',
        display: 'flex',
        flexDirection: 'column',
        paddingTop: 100,
        alignItems: 'center',
        color: '#ffffff',
      }}
    >
      <form onSubmit={editMode ? handleEdit : onSubmit}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
          <Box
            sx={{
              width: 500,
              maxWidth: '100%',
            }}
          >
            <TextField
              fullWidth
              label='fullWidth'
              id='fullWidth'
              type='text'
              value={text}
              placeholder='Enter a todo...'
              onChange={(e) => setText(e.target.value)}
              required
              sx={{ input: { color: '#fff' } }}
            />
          </Box>

          <Button variant='contained' disabled={loading} type='submit'>
            {editMode ? 'Edit' : 'Add'}
          </Button>
        </div>
      </form>
      <div>
        {loading && <p>Loading...</p>}
        {todos?.length > 0 &&
          todos.map((todo) => {
            return (
              <div key={todo._id}>
                <div>{todo.todo}</div>
                <div
                  onClick={() => {
                    setEditMode(true);
                    setEditId(todo._id);
                    setText(todo.todo);
                  }}
                >
                  <EditIcon />
                </div>
                <div onClick={() => handleDelete(todo._id)}>
                  <DeleteIcon />
                </div>
              </div>
            );
          })}
      </div>
    </div>
  );
};

export default Todo;
